mapper.select:
select bno, title, content, writer, view_cnt, comment_cnt, reg_date, up_Date
from board
where bno = #{bno}

mapper.update:
update board
set  title = #{title}, content = #{content}, up_date = now()
where bno = #{bno} and writer = #{writer}

update board
set view_cnt = view_cnt + 1
where bno = #{bno}
